	$: sectTwo = false;
	$: sectThree = false;

	class SW{
		static switches(s1,s2,s3,s4){
			sectOne = s2 
			sectTwo = s3
			sectThree = s4
			home = s1

		}

	}
	class Section{
		home() {
			SW.switches(true, false, false, false)
			console.log("home clicked")
		}
		oSectOne() {
			SW.switches(false, true, false, false)			
		}
		oSectTwo() {
			SW.switches(false, false, true, false)
		}
		oSectThree() {
			SW.switches(false, false, false, true)		
		}
	}

